import React, {useEffect, useState} from 'react';
import BooksCard from './BooksCard';
import './App.css';

function App() {
  const [books, setBooks] = useState([])

  useEffect(() => {
    fetch('http://localhost:3000/popularBooks')
    .then(r => r.json())
    .then(book => setBooks(book))
  }, [])
  return (
    <div className="App">
      <BooksCard key={book.id} name={book.name} author={book.author} image={book.imgURL}/>
    </div>
  );
}

export default App;
