        return (
            <BooksCard key={book.id} book={book} handleFavoriteBook={handleFavoriteBook}/>
        )
    })

    return ( 
        <div>
            <h1 style={{ color: "white"}}> All Books</h1>
            {mappedBooks}
        </div>
    )
};

export default Books
